const body = $json.body || $json;

if (!body.ingredients || !Array.isArray(body.ingredients) || body.ingredients.length === 0) {
  throw new Error('No ingredients provided in request body.');
}

body.ingredients = body.ingredients.map((ing) => {
  const { isEditMode, isUnitDropdownOpen, ...rest } = ing;
  return rest;
});

if (body.cookingTime) body.cookingTime = String(body.cookingTime).toLowerCase();
if (body.cuisine) body.cuisine = String(body.cuisine).toLowerCase();
if (body.dietPreferences) body.dietPreferences = String(body.dietPreferences).toLowerCase();

if (typeof body.portionsAmount !== 'number' || body.portionsAmount <= 0) {
  body.portionsAmount = 2;
}

if (typeof body.cooksAmount !== 'number' || body.cooksAmount <= 0) {
  body.cooksAmount = 1;
}

return [{ json: body }];

let raw = $json.output ?? $json.result ?? $json;

if (typeof raw === 'string') {
  let text = raw.trim();

  // Entferne Code-Fences: ```json ... ``` oder ``` ... ```
  if (text.startsWith('```')) {
    const firstNewline = text.indexOf('\n');
    if (firstNewline !== -1) {
      text = text.slice(firstNewline + 1);
    }

    const fenceIndex = text.lastIndexOf('```');
    if (fenceIndex !== -1) {
      text = text.slice(0, fenceIndex);
    }
  }

  text = text.trim();

  try {
    raw = JSON.parse(text);
  } catch (e) {
    throw new Error('LLM did not return valid JSON: ' + e.message);
  }
}

if (!Array.isArray(raw)) {
  throw new Error('Expected an array of recipes from LLM');
}

if (raw.length > 3) {
  raw = raw.slice(0, 3);
}

return raw.map((recipe) => ({ json: recipe }));
